import classes from "./MyExperience.module.css";
import graduation from "../../../assets/graduation.webp";
import React from "react";
import CompanyList from "./CompanyList";
import { useState } from "react";

const MyExperience = () => {
  //const [showCompany, setShowCompany] = useState(false);
  //const [minimizeList, setMinimizeList] = useState(true);
  let showCompany = false; 
  const btnHandler = () =>{
    showCompany = !showCompany;
  }

  return (
    <div className={classes.projects}>
      <div className={classes.row}>
        <div className={classes.column}>
          <img src={graduation} />
        </div>
        <div className={classes.heading}>
          <h1>Experience</h1>
          <h2>Work &#38; Internships</h2>
          <p>
            My projects makes use of vast variety of latest technology tools. My
            best experience is to create the dynamic Web Apps(SPA) including
            Frontend &#38; Backend and also the Deep Learning projects that
            solves real world problem and deploy them to web applications using
            cloud infrastructure.
          </p>
        </div>
      </div>
      <div className={classes.jobs}>
        <button onClick={btnHandler} className={classes.btn}>Work</button>
        {/* <div className={classes.company}>
            <p>Icici bank</p>
            <p>Quantiphi Inc.</p>
        </div> */}
        { !showCompany && minimizeList && <CompanyList />}
      </div>
      
    </div>
  );
};

export default MyExperience;
